const MongoClient = global.get('mongodb');
const insertData = msg.insertData || {};  // Eklenecek veriyi msg.insertData iinden alyoruz

if (!MongoClient) {
    node.error('MongoDB module not found in global context.');
    return;
}

const user = mongoData.user;
const password = mongoData.pass;
const server = mongoData.server;
const database = mongoData.dataBase;
const yourCollection = mongoData.collection;
// Doru ekilde veritaban ad ile eletiinden emin olun

(async () => {
    // MongoDB balant URL'si
    const url = `mongodb://${user}:${password}@${server}/${database}?authSource=admin`;
   // node.warn(url)
    let client;

    try {
        // MongoDB balants kurma


        client = new MongoClient(url, {
            useNewUrlParser: true,
            useUnifiedTopology: true
        });

        // Connect the client
        await client.connect();

        // Veritabanna balanma

        const db = client.db(database);

        // Belgeyi koleksiyona ekleme (insertOne)
        const result = await db.collection(yourCollection).insertOne(insertData);  // Burada 'yourCollection' koleksiyon adn deitirin

        // Baarl ilem sonras sonucu payload'a ekleyip ilk ka gnderin
        msg.payload = result;

        node.send([msg, null]);

    } catch (err) {
        // Balant veya sorgu hatalarn loglayp ikinci ka gnderin

        msg = {
            error: 'MongoDB insert error: ' + err.message
        };
        node.send([null, msg]);

    } finally {
        // Balanty kapatn

        if (client) {

            try {
                await client.close();
                // node.warn('MongoDB connection closed.');
            } catch (closeErr) {
                msg = {
                    error: 'Error closing MongoDB connection: ' + closeErr.message
                };
                node.send([null, msg]);
            }
        }
    }
})();

(async function () {
    try {
        const http = global.get('http');
        const { parse } = global.get('url');

        // Base URL ve endpoint
        const baseUrl = env.get('SFORIN_URL')

        const urlPath = env.get('logUrlPath');
        const fullUrl = `${baseUrl}${urlPath}`;

        // Global deikenlerden alnan token
        const token = global.get("SforinToken");

        // Varsaylan ablon (Default deerler)
        const defaultTemplate = {
            dataKey: "",
            dataType: 0,
            date: new Date().toISOString(),
            message: "",
            jobResult: 0,
            integrationName: "",
            httpStatusCode: 200,
            duration: null,
            additionalData: "",
            flowName: env.get('sourceModule/Flow'),
            requestPayload: "",
            responsePayload: ""
        };

        // Null, undefined ve bo deer kontrol yapan yardmc fonksiyon
        function isValid(value) {
            return value !== undefined && value !== null && value !== "";
        }

        // Gelen veriyi ileme
        const incomingDataArray = msg.logData || [];

        // Gelen nesneleri eletir
        const mappedData = incomingDataArray.map(item => {
            return {
                dataKey: isValid(item?.dataKey) ? item.dataKey : defaultTemplate.dataKey,
                dataType: isValid(item?.dataType) ? item.dataType : defaultTemplate.dataType,
                date: isValid(item?.date) ? item.date : defaultTemplate.date,
                message: isValid(item?.message) ? item.message : defaultTemplate.message,
                jobResult: isValid(item?.jobResult) ? item.jobResult : defaultTemplate.jobResult,
                integrationName: isValid(item?.integrationName) ? item.integrationName : defaultTemplate.integrationName,
                httpStatusCode: isValid(item?.httpStatusCode) ? item.httpStatusCode : defaultTemplate.httpStatusCode,
                duration: isValid(item?.duration) ? item.duration : defaultTemplate.duration,
                additionalData: isValid(item?.additionalData) ? item.additionalData : defaultTemplate.additionalData,
                flowName: isValid(item?.flowName) ? item.flowName : defaultTemplate.flowName,
                requestPayload: isValid(item?.requestPayload) ? item.requestPayload : defaultTemplate.requestPayload,
                responsePayload: isValid(item?.responsePayload) ? item.responsePayload : defaultTemplate.responsePayload
            };
        });

        // Yeni payload olutur
        const payload = {
            requestId: "",
            sender: "NODE-RED",
            data: mappedData,
            current: [
                {
                    key: "",
                    value: ""
                }
            ]
        };

        // HTTP istei gnderme fonksiyonu
        const sendHttpRequest = (url, method, headers, body) => {
            return new Promise((resolve, reject) => {
                const parsedUrl = parse(url); // URL'yi ayrtryoruz.
                const options = {
                    hostname: parsedUrl.hostname,
                    port: parsedUrl.port || 80, // HTTP iin varsaylan port
                    path: parsedUrl.path,
                    method: method,
                    headers: headers
                };

                const req = http.request(options, (res) => {
                    let responseData = "";
                    res.on("data", (chunk) => {
                        responseData += chunk;
                    });
                    res.on("end", () => {
                        resolve(responseData);
                    });
                });

                req.on("error", (e) => {
                    reject(e);
                });

                if (body) {
                    req.write(JSON.stringify(body));
                }
                req.end();
            });
        };

        // HTTP isteini yap
        const response = await sendHttpRequest(
            fullUrl,
            "POST",
            {
                "Authorization": `Bearer ${token}`,
                "Content-Type": "application/json"
            },
            payload
        );

        // Yant ileme
        msg.payload = JSON.parse(response);
        return msg;

    } catch (error) {
        // Hata durumunda loglama ve mesaj dndrme
        node.error("Bir hata olutu: " + error.message);
        msg.error = error.message;
        return msg;
    }
})();

