            for (let i = 0; i < item.barcodes.length; i++) {
                let query = `
                DECLARE @BARKOD VARCHAR(MAX)='${item.barcodes[i].barcode}'
                DECLARE @STOK_KODU VARCHAR(MAX)='${item.code}'
                IF NOT EXISTS (SELECT 1 FROM TBLSTOKBAR A
                LEFT JOIN TBLSTSABIT B ON A.STOK_KODU = B.STOK_KODU 
                WHERE @BARKOD IN (A.BARKOD, B.BARKOD1, B.BARKOD2, B.BARKOD3) AND A.STOK_KODU = @STOK_KODU)
                BEGIN 
                    DECLARE @OLCU_BR VARCHAR(MAX)
                    DECLARE @OLCU_BR_ VARCHAR(MAX)='AD'

                    SELECT 
                        @OLCU_BR = CASE WHEN OLCU_BR1 = @OLCU_BR_ THEN 1
                                        WHEN OLCU_BR2 = @OLCU_BR_ THEN 2
                                        WHEN OLCU_BR3 = @OLCU_BR_ THEN 3
