const user = mssqlData.user;
const password = mssqlData.pass;
const server = mssqlData.ip;
const database = mssqlData.database; // Make sure this is correctly mapped to the database name
const config = {
    user: user,
    password: password,
    server: server,
    database: database,
    options: {
        encrypt: false,  // Set to true for Azure or encrypted connections
        enableArithAbort: true  // To handle certain arithmetic operations more safely
    }
};
// Log the found config
// node.warn(config);
(async () => {
    // MSSQL configuration

    let pool;

    try {
        // Establishing connection to the MSSQL server
        pool = await sql.connect(config);
        // Execute the query and retrieve results
        const result = await pool.request().query(query);

        // Return the result to the first output
        msg.payload = result;
        node.send([msg, null]);

    } catch (err) {
        // Log connection or query errors and send to second output
        msg = { ...msg,
            error: 'MSSQL connection/query error: ' + err.message
        };
        msg.errorDescription = msg.error;
        node.send([null, msg]);

    } finally {
        // Ensure connection is closed to avoid potential memory leaks
        if (pool) {
            try {
                await pool.close();
                // node.warn('MSSQL connection closed.');
            } catch (closeErr) {
                // node.error('Error closing MSSQL connection: ' + closeErr.message);
                msg = {
                    error: 'Error closing MSSQL connection: ' + closeErr.message
                };
                msg.errorDescription = msg.error;
                node.send([null, msg]);
            }
        }
    }
})();

