info:
tblstsabit iin aktarm yapar ve  S4IN_INTEGRATION_SYNC  tablosuna kayt  gunccler 
ALTER TRIGGER [dbo].[S4IN_StockChanges1]
ON [dbo].[TBLSTSABIT]
FOR INSERT, UPDATE, DELETE
NOT FOR REPLICATION
AS
BEGIN
    DECLARE @datakey VARCHAR(50),
            @datatypel INT,
            @proc VARCHAR(50),
            @msg VARCHAR(255),
            @status INT;

    SET @datatypel = (SELECT ID FROM S4IN_SyncTableType WHERE Name = 'TBLSTSABIT');

    -- Cursor tanmlama
    DECLARE stock_cursor CURSOR FOR 
    SELECT INSERTED.STOK_KODU, CASE 
                          WHEN NOT EXISTS (SELECT 1 FROM DELETED WHERE DELETED.STOK_KODU = INSERTED.STOK_KODU) THEN 1
                          WHEN EXISTS (SELECT 1 FROM INSERTED) AND EXISTS (SELECT 1 FROM DELETED) THEN 0
                          WHEN NOT EXISTS (SELECT 1 FROM INSERTED) THEN -1
                      END AS OperationType
    FROM INSERTED
    FULL OUTER JOIN DELETED
    ON INSERTED.STOK_KODU = DELETED.STOK_KODU;

    -- Cursor kullanm
    OPEN stock_cursor;
    FETCH NEXT FROM stock_cursor INTO @datakey, @status;

    WHILE @@FETCH_STATUS = 0
    BEGIN
        BEGIN TRY
            IF EXISTS (SELECT 1 FROM S4IN_INTEGRATION_SYNC WHERE Datakey = @datakey AND DataTypeID = @datatypel)
            BEGIN
                UPDATE S4IN_INTEGRATION_SYNC
                SET Synctime = NULL,
                    ProcedureID = CASE
                                    WHEN @status = -1 THEN (SELECT ID FROM S4IN_ProcedureType WHERE Name = 'DELETE')
                                    WHEN @status = 0 THEN (SELECT ID FROM S4IN_ProcedureType WHERE Name = 'UPDATE')
                                END
                WHERE Datakey = @datakey AND DataTypeID = @datatypel;
            END
            ELSE
            BEGIN
                INSERT INTO S4IN_INTEGRATION_SYNC (Datakey, DataTypeID, Synctime, ProcedureID)
                VALUES (
                    @datakey,
                    @datatypel,
                    CASE WHEN @status = -1 THEN GETDATE() ELSE NULL END,
                    CASE
                        WHEN @status = -1 THEN (SELECT ID FROM S4IN_ProcedureType WHERE Name = 'DELETE')
                        WHEN @status = 0 THEN (SELECT ID FROM S4IN_ProcedureType WHERE Name = 'UPDATE')
                        ELSE (SELECT ID FROM S4IN_ProcedureType WHERE Name = 'INSERT')
                    END
                );
            END
        END TRY
        BEGIN CATCH
            INSERT INTO S4IN_INTEGRATION_SYNC (Datakey, DataTypeID, Synctime, ProcedureID, ErrorInfo)
            VALUES (
                @datakey,
                @datatypel,
                GETDATE(),
                (SELECT ID FROM S4IN_ProcedureType WHERE Name = 'ERROR'),
                ERROR_MESSAGE()
            );
        END CATCH;

        FETCH NEXT FROM stock_cursor INTO @datakey, @status;
    END

    CLOSE stock_cursor;
    DEALLOCATE stock_cursor;
END;

