CREATE PROCEDURE SP_S4IN_UPDATE_FATUIRS
    @FisNo NVARCHAR(20),   -- Fi numaras parametresi
    @CariKodu NVARCHAR(50) -- Cari kod parametresi
AS
BEGIN
    SET NOCOUNT ON;

	 DECLARE @TotalAmount DECIMAL(18, 2);  -- Toplam Tutar  
	DECLARE @GrossAmount DECIMAL(18, 2);  -- Brt Tutar  
	DECLARE @TotalVAT DECIMAL(18, 2);     -- Toplam KDV  

    BEGIN TRY
        -- 1. Toplam tutar hesapla ve deikene ata
        SELECT 
            @TotalAmount = ISNULL(SUM((STHAR_NF * STHAR_GCMIK) * (1 + STHAR_KDV / 100.0)), 0),
			@GrossAmount = ISNULL(SUM(STHAR_BF * STHAR_GCMIK ), 0),
			@TotalVAT=ISNULL( SUM((STHAR_NF * STHAR_GCMIK)*(STHAR_KDV/100) ),0)
        FROM TBLSTHAR WITH (NOLOCK)
        WHERE FISNO = @FisNo
        AND STHAR_ACIKLAMA = @CariKodu; 

        BEGIN TRANSACTION;

        -- 3. GENELTOPLAM ve brt tutar  alann gncelle
        UPDATE TBLFATUIRS
        SET GENELTOPLAM = @TotalAmount,
		 BRUTTUTAR = @GrossAmount,
		 KDV=@TotalVAT
        WHERE FATIRS_NO = @FisNo
        AND CARI_KODU = @CariKodu;       
        COMMIT TRANSACTION;       

    END TRY
    BEGIN CATCH

        ROLLBACK TRANSACTION;


    END CATCH
END;

