}

// Balant bilgileri
const user = mongoData.user ? encodeURIComponent(mongoData.user) : '';
const password = mongoData.pass ? encodeURIComponent(mongoData.pass) : '';
const host = mongoData.server;
const database = mongoData.database;

if (!host) {
    node.error("Server information is missing. Available fields: " + Object.keys(mongoData).join(", "));
    return null;
}

// Kullanc/ifre yoksa URI'yi ona gre kur
const hasAuth = !!(user && password);
const creds = hasAuth ? `${user}:${password}@` : '';
const uri = `mongodb://${creds}${host}/${database}${hasAuth ? '?authSource=admin' : ''}`;

// Parametreler
const IsUpdated = !!env.get('IsUpdated');         // updateMany modu
const IsDelete = !!env.get('IsDelete');          // delete modu

const rawQuery = (msg.query && msg.query !== "") ? msg.query : env.get('query');

if (isDubug) {
    msg.reqQuery = rawQuery;
    msg.reqFlags = { IsUpdated, IsDelete };
}

// Yardmc: gvenli JSON parse
function parseJSONSafe(v) {
    if (typeof v === "string") {
        try { return JSON.parse(v); } catch (e) {
            throw new Error("Query JSON parse failed: " + e.message);
        }
    }
    return v;
}

// Ana ilem
let client;
try {
    mongodb.connect(uri, { useNewUrlParser: true, useUnifiedTopology: true })
        .then(_client => {
            client = _client;
            const db = client.db(database);
            const coll = db.collection(collectionName);

            // 1) UPDATE
            if (IsUpdated) {
                // Beklenen format: { update: { filter: {...}, update: {...}, options?: {...} } }
                const q = parseJSONSafe(rawQuery);
                const upd = q?.update;
                if (!upd || !upd.filter || !upd.update) {
                    throw new Error("Update mode requires: { update: { filter: {...}, update: {...}, options?: {...} } }");
                }
                const { filter, update, options } = upd;
                return coll.updateMany(filter, update, options || {});
            }

            // 2) DELETE
            if (IsDelete) {
                // Desteklenen formatlar:
                //  a) Basit filtre: { Location: "..." }
                //  b) Ayrntl: { filter: {...}, options?: {...} }
                const del = parseJSONSafe(rawQuery) || {};
                const filter = del.filter ?? del; // filter alan yoksa objenin kendisini filtre say
                const options = del.options || {};               

                if (!filter || typeof filter !== 'object' || Array.isArray(filter)) {
                    throw new Error("Delete filter must be an object. Given: " + JSON.stringify(filter));
                }


                return coll.deleteMany(filter, options);
            }

            // 3) AGGREGATE (default)
            // Beklenen: pipeline array (r. [{ $match: {...} }, { $project: {...} }])
            const pipeline = parseJSONSafe(rawQuery);
            if (!Array.isArray(pipeline)) {
                throw new Error("Aggregate mode requires an array pipeline. Given: " + typeof pipeline);
            }
            return coll.aggregate(pipeline).toArray();
        })
        .then(result => {
            // Baarl sonu
            msg.payload = result;
            node.send([msg, null]);
        })
        .catch(err => {
            // Hata durumu
            msg.error = 'MongoDB Error: ' + err.message;
            msg.errorDescription = msg.error;
            node.send([null, msg]);
            node.error(err.message);
        })
        .finally(() => {
            if (client) {
                try { client.close(); } catch (e) { /* ignore */ }
            }
        });
} catch (err) {
    // Genel hata
    msg.error = 'Connection Error: ' + err.message;
    msg.errorDescription = msg.error;
    node.send([null, msg]);
    node.error(err.message);
}

