            const coll = db.collection(env.get('collection'));
            if (env.get('IsUpdated'))
            {
                // Gelen update objesi: { filter: {...}, update: {...}, options: {...} }
                const updateData = typeof query === "string" ? JSON.parse(query) : query;
                const { filter, update, options } = updateData.update;

                return coll.updateMany(filter, update, options || {});
            }
            // aggregate arsn modern API'ye uygun hale getirelim
            return coll.aggregate(JSON.parse(query)).toArray();
        })
        .then(result => {
