CREATE  FUNCTION IS_EINVOICE_CUSTOMER (@CARI_KOD NVARCHAR(50))      
RETURNS BIT      
AS      
BEGIN      
    DECLARE @Result BIT;      
      
    IF EXISTS (  
        SELECT 1  
        FROM NETSIS..TBLEFATCARI A WITH (NOLOCK)  
        WHERE A.AKTIF = 'E'  
          AND (  
                EXISTS (  
                    SELECT 1  
                    FROM TBLCASABIT B WITH (NOLOCK)  
                    WHERE A.IDENTIFIER = B.VERGI_NUMARASI  
                      AND B.CARI_KOD = @CARI_KOD  
                )  
             OR EXISTS (  
                    SELECT 1  
                    FROM TBLCASABITEK C WITH (NOLOCK)  
                    WHERE A.IDENTIFIER = C.TCKIMLIKNO  
                      AND C.CARI_KOD = @CARI_KOD  
                )  
            )  
    )  
        SET @Result = 1;  
    ELSE  
        SET @Result = 0;  
  
    RETURN @Result;  
END;

