];
const requiredFieldsFatItem = [
    { path: "StokKodu", message: "Kalem  sirasi (value) olan   StokKodu zorunlu." },
    { path: "STra_GCMIK", message: "Kalems iindeki (value)  STra_GCMIK zorunlu." },
    { path: "Sira", message: "Kalems iindeki (value)  Sira zorunlu." },
    { path: "Olcubr", message: "Kalems iindeki (value)  Olcubr zorunlu." },    
    { path: "DEPO_KODU", message: "Kalems iindeki (value)  DEPO_KODU zorunlu." },
];

// Hatalar toplamak iin liste
let errors = [];

// JSON iinde bir alan bulmak iin yardmc fonksiyon
function getValue(obj, path) {

    return path.split('.').reduce((o, key) => o && o[key], obj);
}

// Alanlar kontrol edin
requiredFieldsFatUst.forEach(field => {
    const value = getValue(msg.payload, field.path);

    if (value === null || value === undefined || value === "") {
        errors.push(field.message);
    }
});



msg.payload.Kalems.forEach(item => {

    var hasStockCode=true; // stok kodu giilmeyen kalemlerde sra numarasna gre dn deere verilecek
    requiredFieldsFatItem.forEach(filedItem => {
        var message;
        const value = getValue(item, filedItem.path);
        if (value === null || value === undefined || value === "") {

            if (filedItem.path === "StokKodu") {
                message =  filedItem.message.replace("(value)", item?.Sira)
                hasStockCode=false;
            }
            else {
                message = filedItem.message.replace("(value)", hasStockCode ? "Stok Kodu:"+item?.StokKodu: "Sra No:"+item.Sira)
            }
            errors.push(message)
        }
    })
});

// Hata varsa, HTTP 400 yant ile hata mesajlarn dndrn
if (errors.length > 0) {

    msg.statusCode = 400;
    msg.payload = {
    Type: 2, //"ERROR",    
    Sender: "Node-Red-S2NSALESWAYBILLV2",
    Detail:"Zorunlu Alan Kontrol",
    DefinitionLang: "Sorgu sirasinda hata",
    Data: errors,
    Module: "Node-Red-S2NSALESWAYBILLV2"
};
msg.statusCode = 400;
