const MongoClient = global.get('mongodb');
const insertData = msg.insertData || {};  // Eklenecek veriyi msg.insertData iinden alyoruz

if (!MongoClient) {
    node.error('MongoDB module not found in global context.');
    return;
}

const user = mongoData.user;
const password = mongoData.pass;
const server = mongoData.server;
const database = mongoData.dataBase;
const yourCollection = mongoData.collection;
// Doru ekilde veritaban ad ile eletiinden emin olun

(async () => {
    // MongoDB balant URL'si
    const url = `mongodb://${user}:${password}@${server}/${database}?authSource=admin`;
   // node.warn(url)
    let client;

    try {
        // MongoDB balants kurma


        client = new MongoClient(url, {
            useNewUrlParser: true,
            useUnifiedTopology: true
        });

        // Connect the client
        await client.connect();

        // Veritabanna balanma

        const db = client.db(database);

        // Belgeyi koleksiyona ekleme (insertOne)
        const result = await db.collection(yourCollection).insertOne(insertData);  // Burada 'yourCollection' koleksiyon adn deitirin

        // Baarl ilem sonras sonucu payload'a ekleyip ilk ka gnderin
        msg.payload = result;

        node.send([msg, null]);

    } catch (err) {
        // Balant veya sorgu hatalarn loglayp ikinci ka gnderin

        msg = {
            error: 'MongoDB insert error: ' + err.message
        };
        node.send([null, msg]);

    } finally {
        // Balanty kapatn

        if (client) {

            try {
                await client.close();
                // node.warn('MongoDB connection closed.');
            } catch (closeErr) {
                msg = {
                    error: 'Error closing MongoDB connection: ' + closeErr.message
                };
                node.send([null, msg]);
            }
        }
    }
})();

